mapper.select:
select distinct a.*, b.lat, b.lng
from apt a, aptxy b
where a.dong = b.dong
and a.addr1 = b.addr1
and a. apt = #{aptStr}
and a. area = #{areaStr}

select apt, price, conym
from apt 
where apt = #{aptStr}
and area = #{areaStr}
and gubun = '매매'

select apt, deposit, conym
from apt 
where apt = #{aptStr}
and area = #{areaStr}
and gubun = '전세'
