mapper.select:
	select tt.*
	from (
		select rownum as rn, t.*
		from (
			select distinct a.apt, a.gu, a.street, a.dong, a.addr1, a.area, b.lat, b.lng
			from apt a, aptxy b
			where a.dong = b.dong
			and a.addr1 = b.addr1
			and a.gu = #{searchKey}
			and ((a.dong like '%' || #{searchStr} || '%')
 				or (a.street like '%' || #{searchStr} || '%')
				or (a.apt like '%' || #{searchStr} || '%')) 
			order by apt asc, dong asc, street asc
			) t
		) tt

		select tt.*
		from (
			select rownum as rn, t.*
			from (
				select distinct a.apt, a.gu, a.street, a.dong, a.addr1, a.area, b.lat, b.lng
				from apt a, aptxy b
				where a.dong = b.dong
				and a.addr1 = b.addr1
				and a.gu = #{searchKey}
				and ((a.dong like '%' || #{searchStr} || '%')
	 				or (a.street like '%' || #{searchStr} || '%')
					or (a.apt like '%' || #{searchStr} || '%')) 
				order by apt asc, dong asc, street asc
				) t
			) tt

select distinct a.*, b.lat, b.lng
from apt a, aptxy b
where a.dong = b.dong
and a.addr1 = b.addr1
and a. apt = #{aptStr}
and a. area = #{areaStr}

select apt, price
from apt 
where apt = #{aptStr}
and area = #{areaStr}
and gubun = '매매'

select apt, deposit
from apt 
where apt = #{aptStr}
and area = #{areaStr}
and gubun in ('전세', '월세')
